localStrategies()

function localStrategies() {
    const opts: any = {};
    const cookieExtractor = (ctx) => {
        try {
            return ctx.cookies.request.headers.cookie.split(';')[0].split('token=Bearer ')[1]
        } catch (e) {
            return false
        }
    };
    opts.secretOrKey = config.JWT_SECRET;
    opts.jwtFromRequest = cookieExtractor;
    passport.use(new Jwt.Strategy(opts, async (payload, done) => {
        const {id} = payload;
        const user: IUser = await userModel.getSingleUser(id);
        if(!user) {
            return done(null, false)
        }
        if (user) {
            return done(null, _.pick(user, ['id', 'name']))
        }
    }));
    passport.use('jwt-refresh', new CustomStrategy(
        async (req, cb) => {
            try {
                const refreshToken = req.header.cookie.split('refreshToken=')[1].split(';')[0];
                const user: IUser = await authService.getUserByRefreshToken(refreshToken);
                if(!user) {
                    return cb(null, false)
                }
                const newRefreshToken = await authService.createJWTRefreshToken(user.id);
                await authModel.removeRefreshToken(refreshToken);
                const userPayload = _.pick(user, ['id', 'name']);
                const token = authService.createJWTToken(userPayload);
                authService.setAuthTokens(token, newRefreshToken, req.ctx)
                return cb(null, userPayload);
            } catch (e) {
                return cb(null, false);
            }
        }
    ));
}

export default passport

import * as Knex from 'knex'
import * as bcrypt from 'bcryptjs'
import knex from '../../libs/knex'
import {IUser} from './interfaces/Iusers'

function getSingleUser(id: string): Promise<IUser> {
  return knex('users')
      .select('*')
      .where({id: parseInt(id, 10)})
      .first()
}

function confirmEmail(email: string): Knex.QueryBuilder {
    return knex.raw(`
        update users set
        local = local || '{"verified": true}'::jsonb
        where local->>'email'=?
        returning id, local
        ;
    `, [email]).then(({rows})=> rows[0]);
}

function getSingleUserByEmail(email: string): IUser {
  return knex('users')
      .select('*')
      .whereRaw(`local->>'email'=?`, [email])
      .then(res => res[0])
}

function addLocalUser(user: {name: string, email: string, password: string, verified?: boolean}): Promise<IUser> {
  const hash = hashPassword(user.password)
  return knex('users')
      .insert({
          method: 'local',
          local:
              {
                  email: user.email,
                  name: user.name,
                  password: hash,
                  verified: user.verified || null
              },
      })
      .returning(['id', 'local'])
      .then((res)=> res[0]);
}
function updateLocalPassword(id, password) {
    return knex.raw(`
            update users set
            local = local || '{"password": "${hashPassword(password)}"}'::jsonb
            where id=?
    `, [id])
}
function updateUser(user: IUser): Knex.QueryBuilder {
  user.local.password = hashPassword(user.local.password);
  return knex('users')
      .update(user)
      .where('id', user.id)
      .returning(['name', 'email']);
}

function hashPassword(password: string): string {
  if (!password) return null;
  const salt = bcrypt.genSaltSync();
  return bcrypt.hashSync(password, salt);
}

export {
  updateUser,
  getSingleUser,
  getSingleUserByEmail,
  addLocalUser,
  confirmEmail,
  updateLocalPassword
};

