import {IMessage} from '../db/queries/interfaces/Imessage'
import games from '../db/queries/games'
import {_} from 'lodash'
import {saveMessage, getMessages} from '../db/queries/messages'
import config from '../config'

export default {
    async add(ctx) {
        const data = prepare(ctx.request.body);
        const id = await games.addGame({
            author_id: ctx.req.user.id,
            ...data,
        });
        ctx.ioGeneral.emit('GAME_ADDED', id);
        ctx.status = 201;

        function prepare(body: any) {
            return _.pick(
                body,
                [
                    'players',
                    'additional',
                    'lat',
                    'long',
                    'address',
                    'city_id',
                    'district',
                    'date',
                ].filter((it) => {
                    return body[it] !== ''
                })
            );
        }
    },

  async get(ctx) {
    const {id} = ctx.params;
    ctx.body = await games.get(id);
    ctx.body.fieldNumbersInGame = await games.playerInGame(id);
  },

  async list(ctx) {
    const {id} = ctx.params;
    const {rows} = await games.gamesForCity(id);
    ctx.body = rows;
  },

  async join(ctx) {
    const {id} = ctx.req.user;
    const {gameId, playerNumber} = ctx.request.body;
    const playersInGame = await games.join(id, gameId, playerNumber);
    if (playersInGame) {
      ctx.ioGeneral.emit('PLAYER_JOINED', playersInGame);
    }
    ctx.status = 200;
  },

  async addChatMessage(ctx) {
    const {message, gameId} = ctx.request.body;

    if (!message) {
      ctx.status = 422;
      return;
    }
    const {name, id} = ctx.req.user;
    const savedMessage: IMessage = await saveMessage(message, id, gameId);
    ctx.ioGame
        .to(config.GAME_CHAT_ROOM_PREFIX+gameId)
        .emit('GAME_CHAT_MESSAGE_ADDED', {
          id: savedMessage.id,
          username: name,
          text: savedMessage.text,
          date: savedMessage.date,
        });
    ctx.status = 201
  },

  async getChatHistory(ctx) {
    const {gameId} = ctx.params;
    const messages = getMessages(gameId);
    ctx.ioGame
        .to(config.GAME_CHAT_ROOM_PREFIX+gameId)
        .emit('GAME_CHAT_MESSAGE_HISTORY', await messages);
    ctx.status = 200;
  },
};

import knex from '../../libs/knex'
import {IGame} from './interfaces/Igames'

function addGame(game: IGame.IGame): Promise<number> {
  return knex.transaction(async (trx) => {
    const id: number = await trx.insert(game)
        .into('games')
        .returning('id')
        .then((res) => res[0]);
    await trx.insert(
        {
          player_id: game.author_id,
          player_field_number: 1,
          game_id: id
        })
        .into('games_composition');
    return id;
  });
}

function gamesForCity(cityId: number): Promise<{rows: any}> {
  return knex.raw(
      `
    select g.id,
           c.title_ru as city,
           g.address,
           g.players,
           g.district,
           g.additional,
           g.date,
           gp.active_players as "activePlayers"
    from games as g
    join _cities c on g.city_id = c.city_id
    left join
      (
        select
          coalesce(count(game_id), 0) as active_players,
          game_id
        from games_composition group by game_id
      )
    gp on gp.game_id = g.id
    where c.city_id = ?
    and g.status = 'forming'
    and g.date > CURRENT_TIMESTAMP + interval '2h'
  `,
      [cityId]
  );
}

/**
 * Getter for games
 */
function get(id: number) {
  return knex.raw(
      `select *,
            c.title_ru as city,
            case when gp.active_players
              is null then 0 else gp.active_players end as active_players
            from games g
              join _cities c on g.city_id = c.city_id
              left join (
                select count(*)::int as active_players, game_id
                from games_composition where game_id = :id
                group by game_id) gp on gp.game_id = g.id where g.id = :id
                  `, {id}
  ).then((res) => res.rows[0]);
}

function playerInGame(gameId: number) {
  return knex.raw(`
        select array_agg(player_field_number)
        from games_composition where game_id = :gameId`, {gameId})
      .then((res)=> res.rows[0].array_agg);
}
/**
 * Joining or leaving the game
 */
async function join(
    playerId: number,
    gameId: number,
    playerFieldNumber: number) {
  if (await checkIfPositionOccupied(playerFieldNumber, gameId, playerId)) {
    return;
  }

  const info:IGame.IInfo = {
    playerFieldNumber,
    gameId,
    playerId
  };
  if (await checkIfAlreadyJoin(playerFieldNumber, gameId, playerId)) {
    await leaveGame(playerFieldNumber, gameId, playerId);
    info.event = 'leaved';
  } else {
    await cleanPrevPosition(gameId, playerId);
    await joinGame(playerFieldNumber, gameId, playerId);
    info.event = 'joined';
  }
  info.fieldNumbersInGame =
    await knex.raw(`
    select array_agg(player_field_number)
    from games_composition where game_id = ?`, gameId)
        .then((res)=> res.rows[0].array_agg);
  const {count} = await countPlayersInGame(gameId);
  return {gameId, players: count, info};
}

function checkIfPositionOccupied(
    playerFieldNumber: number,
